import Foundation

// Simple 8x12 bitmap font patterns for ASCII characters
// Used for Linux text rendering without SDL3_ttf
struct BitmapFont {
    static func getPattern(_ char: Character) -> [[Bool]] {
        let patterns: [Character: [[Bool]]] = [
            // Numbers
            "0": createPattern("01111110 11111111 11000011 11000011 11000011 11000011 11000011 11000011 11000011 11000011 11111111 01111110"),
            "1": createPattern("00110000 01110000 11110000 00110000 00110000 00110000 00110000 00110000 00110000 00110000 11111111 11111111"),
            "2": createPattern("01111110 11111111 11000011 00000110 00001100 00011000 00110000 01100000 11000000 11000000 11111111 11111111"),
            "3": createPattern("01111110 11111111 11000011 00000011 00000011 00111110 00111110 00000011 00000011 11000011 11111111 01111110"),
            "4": createPattern("00000110 00001110 00011110 00110110 01100110 11000110 11111111 11111111 00000110 00000110 00000110 00000110"),
            "5": createPattern("11111111 11111111 11000000 11000000 11111110 11111111 00000011 00000011 00000011 11000011 11111111 01111110"),
            "6": createPattern("01111110 11111111 11000000 11000000 11111110 11111111 11000011 11000011 11000011 11000011 11111111 01111110"),
            "7": createPattern("11111111 11111111 00000110 00001100 00011000 00011000 00110000 00110000 01100000 01100000 01100000 01100000"),
            "8": createPattern("01111110 11111111 11000011 11000011 11000011 01111110 01111110 11000011 11000011 11000011 11111111 01111110"),
            "9": createPattern("01111110 11111111 11000011 11000011 11000011 11111111 01111111 00000011 00000011 11000011 11111111 01111110"),
            
            // Letters A-Z
            "A": createPattern("00110000 01111000 11001100 11001100 11001100 11111100 11111100 11001100 11001100 11001100 11001100 11001100"),
            "B": createPattern("11111100 11111110 11000110 11000110 11000110 11111100 11111100 11000110 11000110 11000110 11111110 11111100"),
            "C": createPattern("01111110 11111111 11000011 11000000 11000000 11000000 11000000 11000000 11000011 11000011 11111111 01111110"),
            "D": createPattern("11111100 11111110 11000110 11000011 11000011 11000011 11000011 11000011 11000011 11000110 11111110 11111100"),
            "E": createPattern("11111111 11111111 11000000 11000000 11000000 11111110 11111110 11000000 11000000 11000000 11111111 11111111"),
            "F": createPattern("11111111 11111111 11000000 11000000 11000000 11111110 11111110 11000000 11000000 11000000 11000000 11000000"),
            "G": createPattern("01111110 11111111 11000011 11000000 11000000 11001111 11001111 11000011 11000011 11000011 11111111 01111110"),
            "H": createPattern("11000011 11000011 11000011 11000011 11000011 11111111 11111111 11000011 11000011 11000011 11000011 11000011"),
            "I": createPattern("11111111 11111111 00111100 00111100 00111100 00111100 00111100 00111100 00111100 00111100 11111111 11111111"),
            "J": createPattern("11111111 11111111 00001100 00001100 00001100 00001100 00001100 11001100 11001100 11001100 11111111 01111110"),
            "K": createPattern("11000011 11000110 11001100 11011000 11110000 11100000 11110000 11011000 11001100 11000110 11000011 11000011"),
            "L": createPattern("11000000 11000000 11000000 11000000 11000000 11000000 11000000 11000000 11000000 11000000 11111111 11111111"),
            "M": createPattern("11000011 11100111 11111111 11011011 11000011 11000011 11000011 11000011 11000011 11000011 11000011 11000011"),
            "N": createPattern("11000011 11100011 11110011 11011011 11011011 11001111 11000111 11000011 11000011 11000011 11000011 11000011"),
            "O": createPattern("01111110 11111111 11000011 11000011 11000011 11000011 11000011 11000011 11000011 11000011 11111111 01111110"),
            "P": createPattern("11111100 11111110 11000110 11000110 11000110 11111110 11111100 11000000 11000000 11000000 11000000 11000000"),
            "Q": createPattern("01111110 11111111 11000011 11000011 11000011 11000011 11000011 11011011 11001111 11111111 01111111 00000011"),
            "R": createPattern("11111100 11111110 11000110 11000110 11000110 11111110 11111100 11001100 11000110 11000110 11000011 11000011"),
            "S": createPattern("01111110 11111111 11000011 11000000 11100000 01111110 00111111 00000111 00000011 11000011 11111111 01111110"),
            "T": createPattern("11111111 11111111 00111100 00111100 00111100 00111100 00111100 00111100 00111100 00111100 00111100 00111100"),
            "U": createPattern("11000011 11000011 11000011 11000011 11000011 11000011 11000011 11000011 11000011 11000011 11111111 01111110"),
            "V": createPattern("11000011 11000011 11000011 11000011 11000011 11000011 11000011 11000011 01100110 00111100 00011000 00011000"),
            "W": createPattern("11000011 11000011 11000011 11000011 11000011 11000011 11011011 11011011 11111111 11100111 11000011 11000011"),
            "X": createPattern("11000011 11000011 01100110 00111100 00011000 00011000 00011000 00111100 01100110 11000011 11000011 11000011"),
            "Y": createPattern("11000011 11000011 01100110 00111100 00011000 00011000 00011000 00011000 00011000 00011000 00011000 00011000"),
            "Z": createPattern("11111111 11111111 00000110 00001100 00011000 00110000 01100000 11000000 11000000 11000000 11111111 11111111"),
            
            // Lowercase letters
            "a": createPattern("00000000 00000000 01111110 11111111 00000011 01111111 11111111 11000011 11000011 11111111 01111111 00000000"),
            "b": createPattern("11000000 11000000 11111100 11111110 11000110 11000110 11000110 11000110 11000110 11111110 11111100 00000000"),
            "c": createPattern("00000000 00000000 01111110 11111111 11000011 11000000 11000000 11000000 11000011 11111111 01111110 00000000"),
            "d": createPattern("00000011 00000011 01111111 11111111 11000011 11000011 11000011 11000011 11000011 11111111 01111111 00000000"),
            "e": createPattern("00000000 00000000 01111110 11111111 11000011 11111111 11111111 11000000 11000000 11111111 01111111 00000000"),
            "f": createPattern("00111110 01111111 01100000 11111110 11111110 01100000 01100000 01100000 01100000 01100000 01100000 00000000"),
            "g": createPattern("00000000 00000000 01111111 11111111 11000011 11000011 11111111 01111111 00000011 11000011 11111111 01111110"),
            "h": createPattern("11000000 11000000 11111100 11111110 11000110 11000110 11000110 11000110 11000110 11000110 11000110 00000000"),
            "i": createPattern("00110000 00110000 00000000 01110000 00110000 00110000 00110000 00110000 00110000 01111110 01111110 00000000"),
            "j": createPattern("00001100 00001100 00000000 00011100 00001100 00001100 00001100 00001100 11001100 11001100 11111111 01111110"),
            "k": createPattern("11000000 11000000 11000110 11001100 11011000 11110000 11110000 11011000 11001100 11000110 11000011 00000000"),
            "l": createPattern("01110000 00110000 00110000 00110000 00110000 00110000 00110000 00110000 00110000 01111110 01111110 00000000"),
            "m": createPattern("00000000 00000000 11101110 11111111 11011011 11011011 11011011 11011011 11000011 11000011 11000011 00000000"),
            "n": createPattern("00000000 00000000 11111100 11111110 11000110 11000110 11000110 11000110 11000110 11000110 11000110 00000000"),
            "o": createPattern("00000000 00000000 01111110 11111111 11000011 11000011 11000011 11000011 11000011 11111111 01111110 00000000"),
            "p": createPattern("00000000 00000000 11111100 11111110 11000110 11000110 11111110 11111100 11000000 11000000 11000000 00000000"),
            "q": createPattern("00000000 00000000 01111111 11111111 11000011 11000011 11111111 01111111 00000011 00000011 00000011 00000000"),
            "r": createPattern("00000000 00000000 11111100 11111110 11000110 11000000 11000000 11000000 11000000 11000000 11000000 00000000"),
            "s": createPattern("00000000 00000000 01111110 11111111 11000000 01111110 00111111 00000011 11000011 11111111 01111110 00000000"),
            "t": createPattern("01100000 01100000 11111110 11111110 01100000 01100000 01100000 01100000 01100000 01111110 00111110 00000000"),
            "u": createPattern("00000000 00000000 11000011 11000011 11000011 11000011 11000011 11000011 11000011 11111111 01111111 00000000"),
            "v": createPattern("00000000 00000000 11000011 11000011 11000011 01100110 01100110 00111100 00111100 00011000 00011000 00000000"),
            "w": createPattern("00000000 00000000 11000011 11000011 11011011 11011011 11111111 11100111 11000011 11000011 11000011 00000000"),
            "x": createPattern("00000000 00000000 11000011 01100110 00111100 00011000 00011000 00111100 01100110 11000011 11000011 00000000"),
            "y": createPattern("00000000 00000000 11000011 11000011 11000011 11111111 01111111 00000011 11000011 11111111 01111110 00000000"),
            "z": createPattern("00000000 00000000 11111111 11111111 00000110 00001100 00011000 00110000 01100000 11111111 11111111 00000000"),
            
            // Punctuation
            ":": createPattern("00000000 00000000 00110000 00110000 00000000 00000000 00000000 00000000 00110000 00110000 00000000 00000000"),
            " ": createPattern("00000000 00000000 00000000 00000000 00000000 00000000 00000000 00000000 00000000 00000000 00000000 00000000"),
            "-": createPattern("00000000 00000000 00000000 00000000 00000000 11111111 11111111 00000000 00000000 00000000 00000000 00000000"),
            "/": createPattern("00000011 00000110 00001100 00011000 00110000 01100000 11000000 11000000 01100000 00110000 00011000 00001100"),
        ]
        
        return patterns[char] ?? Array(repeating: Array(repeating: false, count: 8), count: 12)
    }
    
    private static func createPattern(_ pattern: String) -> [[Bool]] {
        let lines = pattern.split(separator: " ")
        return lines.map { line in
            line.map { $0 == "1" }
        }
    }
}
